function weirdPattern(length) {
  // if (length < 8) {
  //   return;
  // }

  t.pushState();

  for (let i = 0; i < 60; i++) {
    let a = map(millis(), 0, 2000, 0, 3);

    t.moveForward(length * sin(a));
    t.turnRight(random(length));

    t.moveForward(length * sin(a) * 25);
    t.turnLeft(length * 0.5);
  }

  t.popState();
