// followed a tutorial from Schmidty Notes https://www.schmidtynotes.com/blog/p5/2022-03-05-random-vectors/

let particles = [];
let n = 100;
let palettte;
let squiggliness = 1/500;
let lineStroke = 2;
let freq = 4;

function setup(){
createCanvas(600, 600);
  background("black")

  noStroke();
  palette = ["#f2a87e", "#e78d5b", "#d1622b","#a14a21", "#711c02", "#9194E2"]
  updateParticles();
}

function draw(){
  for (let p of particles) {
    p.draw();
    p.move();
    p.stop();
  }
}

function updateParticles(){
  particles = [];
  for(let x = 0; x<width; x+=freq){
    let x_ = x;
    let s_ = lineStroke;
    let cNum = floor(random(palette.length))
    let c_ = color(palette[cNum])
    particles.push(new Particle(x_, 0, s_, c_));
    particles.push(new Particle(x_, height, s_, c_));
  }
  for(let y = 0; y< height; y+=freq){
    let y_ = y;
    let s_ = lineStroke;
    let cNum = floor(random(palette.length))
    let c_ = color(palette[cNum])
    particles.push(new Particle(0, y_, s_, c_));
    particles.push(new Particle(width, y_, s_, c_));
  }
}

class Particle {
  constructor(x_, y_, s_, c_){
    this.x = x_;
    this.y = y_;
    this.size = s_;
    this.c = c_;

    this.alpha = 70; 
    this.dist = 0.75;
  }
  move(){
    let theta = noise(this.x * squiggliness, this.y * squiggliness) * PI * 2;
    let v = p5.Vector.fromAngle(theta, this.dist) 
    this.x += v.x;
    this.y += v.y;
    this.alpha *= 1;

  }
  draw(){
    fill(this.c)
    ellipse(this.x, this.y, this.size)
  }
  stop(){
    if(this.x>width || this.x<0){
      this.dist = 0;
    }
    if(this.y>height || this.height<0){
      this.dist = 0;
    }
  }
}

