let frequencyY = 10;
let midPoint = 0.5;
let palette = ["#03658c", "	#C79A79", "#aeacc1", "#593C2C", "#CFA493", "#7993B6", "#445F3D", "#B18499", "#A57662", "#A6282E"];


function setup() {
