function draw() {
  rectMode(CENTER);

  //draw background

  //set seed variable
  noiseSeed(100);

  // create image
  myImg = createImage(10, 10);
  myImg.loadPixels();

  for (let y = 0; y < myImg.height; y++) {
    for (let x = 0; x < myImg.width; x++) {

// this variable is gnarly i know, i'm sure there's an easier way to do this

      let isInSquare =
        x <
          myImg.width / 2 +
            ((pixelCount/10) * squareSize) / 100 &&
        x >
          myImg.width / 2 -
            ((pixelCount / 10) * squareSize) / 100 &&
        y <
          myImg.height / 2 +
            ((pixelCount / 10) * squareSize) / 100 &&
        y >
          myImg.height / 2 -
            ((pixelCount / 10) * squareSize) / 100;


      let c = null;

      // draw album art + gradient

      if (isInSquare) {
        let n = noise(
          (frequencyX * x) / myImg.width,
          (frequencyY * y) / myImg.height ,
        );

        //multiply n by n and reassign new value to n
        n *= n;

        if (n <= midPoint) {
          n = map(n, 0, midPoint, 0, 1);
          c = lerpColor(color(random(palette)), color(random(palette)), n);
        } else {
          n = map(n, midPoint, 1, 0, 1);
          c = lerpColor(color(random(palette)), color(random(palette)), n);

        }
      } else {
        c = color("#f2274c");
      }

      setQuick(myImg, x, y, c);
    }
  }

  myImg.updatePixels();

  image(myImg, -20, -20, width, height);
  noLoop();

  //draw border 
  push();
  rectMode(CENTER);
  stroke("#BB953F");
  strokeWeight(10);
  noFill();
  rect(width/2+10,height/2+10, squareSize+pixelCount);
  pop();
}

