function setQuick(img, x, y, c) {
  const i = (y * img.width + x) * 4;
  img.pixels[i + 0] = c.levels[0];
  img.pixels[i + 1] = c.levels[1];
  img.pixels[i + 2] = c.levels[2];
  img.pixels[i + 3] = c.levels[3] ? c.levels[3] : 255;
}

