      const snowflake = LAYER[i];
      circle(snowflake.x, snowflake.y, (snowflake.l * MAX_SIZE) / LAYER_COUNT);
      updateSnowflake(snowflake);
    }
  }
}

function drawNoiseBackground(g, c) {
  g.clear();
  let noiz = 0.1;
  let rnd = random(0.000001, 0.00001)
  for (let i = 0; i < 100000; i++) {
    const x = random(1) * width;
    const y = random(1) * height;
    const w = noise(noiz) * random(5, 33);
    const h = noise(noiz) * random(5, 33);
    g.stroke('#C242FF');
    g.strokeWeight(noiz)
    g.fill(c);
    g.ellipse(x, y, w, h);

    noiz += rnd;
  }
}

function updateSnowflake(snowflake) {
  const diameter = (snowflake.l * MAX_SIZE) / LAYER_COUNT;
  if (snowflake.y > height + diameter) snowflake.y = -diameter;
  else snowflake.y += GRAVITY * snowflake.l * snowflake.mass;
}

