function drawBranch(length) {
  if (length < 4) {
    return;
  }

  // draw this branch
  strokeWeight(length / 7);
  stroke(98, 102, 89);
  myTurtle.moveForward(length * 0.7);
  myTurtle.turnLeft(5);
