    myTurtle.moveTo(x, y);
    myTurtle.penDown();


    for (let i = 0; i < 20; i++){
        myTurtle.moveForward(in_diam);
        myTurtle.turnLeft(30);
    }

}

function drawTree(length) {
  if (length < 3) {
    return;
  }

  // draw this branch
  strokeWeight(length / 10);
  tu.moveForward(length * 0.9);
  tu.turnRight(10);
  tu.moveForward(length * 0.5);

  // left child
  tu.pushState();
  tu.turnLeft(random(20, 40));
  drawTree(length * 0.75);
  //drawBranch(length * random(.5, .9));

  tu.popState();

  // right child
  tu.pushState();
  tu.turnRight(random(10, 30));
  drawTree(length * 0.75);
  //drawBranch(length * random(.5, .9));
  tu.popState();
}

