// Began sketch with help from http://paperjs.org/examples/path-simplification/
var path;
var pathCounter = 0;
var pathArray = [];

var pathHue = 10;

function onMouseDown(event) {
	// If we produced a path before, deselect it:
	if (path) {
		path.selected = false;
	}

	// Create a new path and set its stroke color to black:
	path = new Path({segments: [event.point]});
// 	path.strokeColor = 'blue';
	path.strokeWidth = 2;
	path.strokeCap = 'round';
	path.dashArray = [10, 4, 2, 7, 12];
// 	path.fullySelected = true;

}

// While the user drags the mouse, points are added to the path
// at the position of the mouse:
function onMouseDrag(event) {
	path.add(event.point);
}

// When the mouse is released, we simplify the path:
function onMouseUp(event) {

    pathCounter += 1
	// When the mouse is released, simplify it:
// 	path.smooth({ type: 'catmull-rom' });
	path.smooth({ type: 'continuous' });
	path.simplify(100);
// 	path.closed = true;

    for (i = 0; i < 20; i++){

        // Creates a new path from the path that was just drawn
        path[i] = new Path({segments: path.segments});

        // Moves path segments by the amount below
        for (j = 0; j < path.segments.length; j++){
            path[i].segments[j].point += (j * 3) + (i * 2) + Math.random() * 10 - 10
        }

        pathHue += 1

        path[i].strokeColor = 'red';
        path[i].strokeColor.hue += pathHue;
        // path[i].closed = true;

        // Sets random dash pattern
        path[i].dashArray = [Math.random() * 20,
                            Math.random() * 20,
                            Math.random() * 20,
                            Math.random() * 100,
                            Math.random() * 20];
    }


}

