//move the mouse to create different patterns with points in a sphere
function drawSphere(space) {

  orbitControl(10, 10, 15);
  //different colour options
  //stroke(150, 235, int(random(100, 255)));
  fill(int(random(50, 150)), 130, int(random(100, 150)), 150);
  strokeWeight(2);
  //fill(int(random(100, 255)), 190, int(random(100, 255)),150);
  //stroke(int(random(255)),0,int(random(255)))
  //fill(30,int(random(100,255)),int(random(150,255),50))

  for (let a = 0; a < 270; a += space) {
    for (let b = 0; b < 180; b += space) {
      beginShape(POINTS);
      x = 100 * sin(a) * cos(b);
      y = 160 * cos(a);
      z = 100 * cos(a) * sin(b);
      arc(x, y, z, 200, 2, 5);
      endShape();
    }
  }
}

