let yoff = 0.0;
let xoff = 0.0;

function setup() {
  createCanvas(400, 400);
  frameRate(6);
}

function draw() {
  background(5, 3, 50);

  push();
  beginShape();
  fill(0);
  for(let i = 0; i <= width; i+=5){
    let o = map(noise(xoff, yoff), 0, 1, 150, 250);
    vertex(i, o);
    xoff += 0.01;
  }

  yoff += 0.01;
  vertex(width, 100);
  vertex(0, 100);
  endShape();
  pop();

  push();
  fill(0);
  rect(0, 0, width, 100);
  pop();

  push();
  fill(255);
  noStroke();
  for(let u = 0; u < width; u+=20){
    for(let p = 0; p < 10; p++){
      circle(u * random(10), p * random(10), 2);
    }
  }
  pop();

  firework(20000);
}

function firework(time) {
  let a = map(millis(), 0, time, 0, 30);
  let offsetA = sin(a) * 10;
  let offsetB = cos(a) * 10;
  let sizeCirc = (offsetA += offsetB);

  for (let x = 0; x < 20; x++) {
    for (let y = 0; y < 20; y++) {
      push();
      let r, g, b;
      r = map(x, 0, 20, 0, 255);
      g = map(x, 0, 20, 0, 255);
      b = map(x, 0, 20, 0, 255);

      noStroke();
      fill(random(r), random(g), random(b), 100);

      circle(
        random(x * 100 + noise(x)),
        random(y * 10 + noise(y)) + 200,
        sizeCirc
      );
      pop();
    }
  }
}

