let img2;

function preload() {
  img2 = loadImage("download (15).jpeg");
}

function setup() {
  createCanvas(800, 800);
  // pixelDensity(1.5);
  noLoop();
}

function draw() {
  background(0);

  noStroke();
  img2.loadPixels();

  for (let row = 0; row < img2.width; row += 10) {
    for (let col = 0; col < img2.height; col += 10) {
      const c = getQuick(img2, row, col)[0];

      let pixelR = img2.pixels[c];
      let pixelG = img2.pixels[c + 1];
      let pixelB = img2.pixels[c + 2];

      if (pixelR < random(60)) {
        fill(c * 255, 0, 0);
        rect(row * 0.5, col * 0.5, 2.5, 5, 1.5);
      }

      if (c > random(250)) {
        fill(0, c * 255, 0);
        rect(row * 0.5, col * 0.5, 2.5, 5, 1.5);
      }

      if (pixelB > random(255)) {
        fill(0, 0, c * 250);
        rect(row * 0.5, col * 0.5, 2.5, 5, 1.5);
      }

      if (pixelR === 0 && pixelG === 0 && pixelB === 0) {
        fill(0);
        rect(row * 0.5, col * 0.5, 2.5, 5, 1.5);
      }

      // updatePixels();
    }
  }
}

function getQuick(img, x, y) {
  const i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}

function setQuick(img, x, y, c) {
  const i = (y * img.width + x) * 4;

  img.pixels[i + 0] = c[0];
  img.pixels[i + 1] = c[1];
  img.pixels[i + 2] = c[2];
  img.pixels[i + 3] = c[3];
}

