let diameter;
let dotColor;
let x, y;
let gridX, gridY;

let frequency = 0.02;
function setup() {
  createCanvas(410, 410);
}

function draw() {
  background(0);

  noStroke();
  ellipseMode(CENTER);

  let noiseFrequency = 0.02;

  for (let i = 0; i < 400; i++) {
    // these points are not scattered in the same way
    // how can you make the arrangement match the challenge?
    x = noise(i * frequency, 0) * 20;
    y = noise(0, i * frequency) *20; //noise is noise only when each x is very close to each other, otherwise its guassian random

    // the diameter shouldn't always be 10, it needs to vary
    diameter = noise(i * frequency * 3, 0)* 30;
  dotColor = map(diameter, 5, 30, 0, 255);
    // the colors also need to change
    // what colorMode would be easiest to work with?
    colorMode(HSB, 255);
    fill(dotColor, 255, 255, 200);
    gridY = floor(i / 20) * 20;
    gridX = i%20 * 20;


    rect(gridX + x,  gridY + y, diameter, diameter);
  }

  noLoop();
}

