class Particle {
  constructor() {
    this.x = random(width);
    this.y = random(height);
    this.r = random(5,20);
    this.angle=random(0,HALF_PI)
    this.w=random(5,10)
    this.h=random(-5,5)

  }

  display() {
    noStroke()
    let pixelX=floor(this.x/scale)
    let pixelY=floor(this.y/scale)
    let col = img.get(pixelX,pixelY)
    // console.log(col)
    stroke(col[0],col[1],col[2], slider.value());
    strokeWeight(8)
    noFill()
    // ellipse(this.x, this.y, this.r/2, this.r/2);
    arc(this.x,this.y,this.w,this.h,PI,0)
  }

  move() {
    this.x += random(-15, 15);
    this.y += random(-15,15);

    this.x=constrain(this.x,0,width)
    this.y=constrain(this.y,0,height)

  }


}

