function onFrame(event) {
    for (var i = 0; i < stars.length; i++) {
        if (currMousePosition.x > view.size.width/2) {
            if (currMousePosition.y > view.size.height/2) {
                stars[i].translate(stars[i].rate, -stars[i].rateY);
            }
            else {
                stars[i].translate(stars[i].rate, stars[i].rateY);
            }
        }
        else {
            if (currMousePosition.y > view.size.height/2) {
                stars[i].translate(-stars[i].rate, -stars[i].rateY);
            }
            else {
                stars[i].translate(-stars[i].rate, stars[i].rateY);
            }
        }
        if (stars[i].position.x > view.size.width) {
            stars[i].position.x = 0;
        }
        if (stars[i].position.x < 0) {
            stars[i].position.x = view.size.width;
        }
        if (stars[i].position.y > view.size.height) {
            stars[i].position.y = 0;
        }
        if (stars[i].position.y < 0) {
            stars[i].position.y = view.size.height;
        }
    }
}

