  noStroke();
  for (var starty = 0; starty < plank.height; starty++) { 
    for (var startx = 0; startx < plank.width; startx++) {
      var index = (startx + starty * plank.width) * 4;
      var r = plank.pixels[index + 0];
      var g = plank.pixels[index + 1];
      var b = plank.pixels[index + 2];

      var bright = ((0.3 * r) + (0.59 * g) + (0.11 * b)) 


      if (bright < 25.5) {
       fill(color('#3DFAFF'));
      } else if (bright >= 25.5 && bright < 51) {
       fill(color('#48BEFF'));
        rect(startx, starty, size, size)
      } else if (bright >= 51 && bright <= 76.5) {
         fill(color('#E0ACD5'));
        rect(startx, starty, size, size)
       } else if (bright >= 76.5 && bright <= 102) {
         fill(color('#53B3CB'));
         rect(startx, starty, size, size)
       } else if (bright >= 102 && bright <= 127.5) {
         fill(color('#F9C22E'));
         rect(startx, starty, size, size)
       } else if (bright >= 127.5 && bright <= 153) {
         fill(color('#F15946'));
         triangle(startx, starty, startx, starty + size, startx + size, starty)
       } else if (bright >= 153 && bright <= 178.5) {
         fill(color('#E01A4F'));
         triangle(startx, starty, startx, starty + size, startx + size, starty)
       } else if (bright >= 178.5 && bright <= 204) {
         fill(color('#453A49'));
         triangle(startx, starty, startx, starty + size, startx + size, starty)
       } else if (bright >= 204 && bright <= 229.5) {
         fill(color('#282F44'));
         triangle(startx, starty, startx, starty + size, startx + size, starty)
       } else if (bright >= 229.5 && bright <= 255) {
         fill(color('#191D32'));
         triangle(startx, starty, startx, starty + size, startx + size, starty)
       }
      startx = startx + size -1 
    }
    starty = starty + size -1 
  }
}

