// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js

const _branchNum = 48;
const _thickness = 1.2;

var COLORS = ['#f94144', '#f3722c', '#f8961e', '#f9844a', '#f9c74f', '#90be6d', '#43aa8b', '#4d908e', '#577590', '#277da1'];

const _spd = 1;
var _particles = [];

function setup() {
  createCanvas(960, 720);
  angleMode(DEGREES);
  noFill();
  strokeWeight(2);

  COLORS = shuffle(COLORS);

  // create the particles
  for(let col = 0; col < _branchNum; col++) {
    _particles.push(new particle(
      450 + col * _thickness, height, 
      sqrt(col), color(COLORS[round(random(col % COLORS.length))])
    ));
  }
}

function draw() {
  for(let pt in _particles) {
    if(_particles[pt].update()) {
      _particles.splice(pt, 1);
    } else {
      _particles[pt].draw();
    }
  }
}

class particle {
  constructor(x, y, frq, color) {
    this.pos_x = x;
    this.pos_ox = x;
    this.pos_y = y;
    this.dir = map(noise(millis() / 1000), 0, 1, 225, 315);
    this.frq = frq;
    this.color = color;
    this.head = random() > 0.5 ? true : false;
  }

  update() {
    let sec = millis() / 1000;
    let x = this.pos_x, y = this.pos_y, d = this.dir;

    // when reach the branch point start to add to direction
    if(sec > map(abs(this.pos_ox - width/2), 0, 30, 7.2, 1.2)) {
      if(this.head)
        this.dir += map(noise(sec, this.frq), 0, 1, -0.2, 1.2);
      else
        this.dir += map(noise(sec, this.frq), 0, 1, -1.2, 0.2);
    } else {
      this.dir = map(noise(sec), 0, 1, 225, 315);
    }

    this.pos_x += cos(this.dir) * _spd;
    this.pos_y += sin(this.dir) * _spd;

    // check if the particle reach the limit
    if(this.dir < 90 || this.dir > 450)
      return true;
    else return false;
    return false;
  }

  draw() {
    push();
    stroke(this.color);
    point(this.pos_x, this.pos_y);
    pop();
  }
}

