  image( pg2, 0,0, width,height ); // draw new image

  frame += speed / 10;
}

// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js

let frequency_slider;
let amplitude_slider;
let timespeed_slider;

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 50;

let frame = 0;

function setup() {
  createCanvas(500, 300);

  createP("Frequency");
  frequency_slider = createSlider(0, 250, 50);


  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);


  createP("Time Speed");
  timespeed_slider = createSlider(0, 10, 0.02);
}

function draw() {
  background(50);
  ellipseMode(CENTER);
  fill(255);
  noStroke();

  let frequency = frequency_slider.value() / 10;
  let amplitude = amplitude_slider.value() / 100;
  let timespeed = timespeed_slider.value() / 100;

  noiseDetail(1, 0.5);

  frame += timespeed;


  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX = noise(i * frequency + frame) * amplitude * 100;
    let offsetY = noise(i * frequency + frame) * amplitude * 100;


    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js
// code inspiration from Alex Carney https://editor.p5js.org/JAlexCarney/sketches/AE0B4ltjU

let map = null;
let noiseScale = 1/20;
let darkest = "#820A2E";
let dark = "#9E2C6E";
let medium = "#E45DAB";
let light = "FFD6F7";


function setup()
{
  createCanvas(600, 600);

  background(0);

  noiseDetail(20, 0.5);

  makeMap();

  drawMap();
  drawMark();
}


function makeMap(){

// this is how map is stored = [[1,2,3],[4,5,6],[7,8,9]];
  //0 1 2
//0 1 2 3
//1 4 5 6 map[0][0] = 1 // map[0] = [1 2 3] map[0][0] = 1
//2 7 8 9 map[1][1] = 5 // map[1] = [4 5 6] map[1][1] = 5
{
  map = [];
  // cwidth = canvas width, cheight = canvas height
  for(let cWidth = 0; cWidth < width; cWidth++)
  {
    map[cWidth] = [];
    for(let cHeight = 0; cHeight < height; cHeight++)
    {
      //for every cWidth, add cHeight, and for every cwidith and cheight, 1 color is associated with it
      map[cWidth][cHeight] = pickColor(cWidth, cHeight);
    }
  }
}
}

function pickColor(cWidth, cHeight)
{
  let h = noise((cWidth)*noiseScale/3,
               (cHeight)*noiseScale);
  let c = "#facade";

  if(h < 0.3)
  {
    c = darkest;
  } 
  else if(h < 0.5)
  {
    if(random() > pow(h-0.15,20)*0.5)
    {
      c = dark;
    }
    else
    {
      c = light;
    }
  }
  else if(h < 0.8)
  {
    if(random() > pow(h-0.3,3)*0.8)
    {
      c = medium;
    }
    else
    {
      c = light;
    }
