function getColorFromDeck() {
  let v = colorDeck[colorPosition];
  colorPosition++;
  if (colorPosition > colorDeck.length) {
    colorDeck = shuffle(colorDeck);
    v = colorDeck[0];
    colorPosition = 1;
  }
  return v;
}

