function BrownianEllipse() {

  // Shift all elements 1 place to the left of the array
  for ( let i = 1; i < num; i++ ) {
    arrayx[i - 1] = arrayx[i];
    arrayy[i - 1] = arrayy[i];
  }

  // Put a new value at the end of the array
  arrayx[num - 1] += random(-range, range);
  arrayy[num - 1] += random(-range, range);

  // constrain points so they only remain on the screen
  arrayx[num - 1] = constrain(arrayx[num - 1], 0, width);
  arrayy[num - 1] = constrain(arrayy[num - 1], 0, height);

  // Draw a line connecting the points
  for ( let j = 1; j < num; j++ ) {
    //weight of lines
      // strokeWeight(thickSlider.value());
      strokeWeight(params.thickness);

      // set the color of lines
      // stroke(hueSlider.value(), 100, 100);
      stroke(params.col, 100, 100);
    scribble.scribbleLine( arrayx[j - 1], arrayy[j - 1], arrayx[j], arrayy[j] );

  }
}

// my lines function
function overlapping() {
    for ( let j = 1; j < num; j++ ) {
    //weight of lines
      // strokeWeight(thickSlider.value() / 3);
      strokeWeight(params.thickness);

    // set the color of lines
      // stroke(hueSlider.value(), 100, 80);
      stroke(params.col, 100, 100);
      line(mouseX, mouseY, arrayx[j], arrayy[j] );
    }
}

