let dotArr = [];
let noiseFactor = 20;
t = 0;

function setup() {
  createCanvas(windowWidth, windowHeight);

  let noiseFrequency = 100;

  for (let i = 0; i < 100; i++) {
    // these points are not scattered in the same way
    // how can you make the arrangement match the challenge?
    let x = noise(i * noiseFrequency) * width;
    let y = noise(i * noiseFrequency, 50) * height;

    // the diameter shouldn't always be 10, it needs to vary
    let diameter = (random(3, 20) + random(3, 20) + random(3, 20))/3;

    // the colors also need to change
    // what colorMode would be easiest to work with?

    dotArr.push(new Circle(x, y, diameter + random(-2,2), diameter + random(-2,2)));
  }
  background('#fff');
}

function draw() {
  noStroke();
  ellipseMode(CENTER);

  for (let i = 0; i < dotArr.length; i++) {
    dotArr[i].draw();
    const dot = dotArr[i];
    noiseArg = noise(dot.pos.x * noiseFactor, dot.pos.y * noiseFactor);
    dotArr[i].update(noiseArg);
  }

}


class Circle {
  constructor(x,y,d, d1) {
    this.x = x;
    this.y = y;
    this.d = d;
    this.d1 = d1;
    this.pos = createVector(x,y);
    this.prev = createVector(x,y);
    if (d < 12) {
      this.fill = [26, 35, 126];
    }
    else if (d < 15) {
      this.fill = [48, 63, 159];
    }
    else {
      this.fill = [33, 150, 243];
    }
    this.deadCount = 0;
    this.radius = width*0.75;
  }

  draw() {
    fill(this.fill[0], this.fill[1], this.fill[2]);
    ellipse(this.pos.x, this.pos.y, this.d, this.d1);
  }

  update(noiseV) {
    this.v = p5.Vector.fromAngle(noiseV * TWO_PI, this.deadCount * TWO_PI);
    this.prev = this.pos.copy();
	this.pos = this.pos.add(this.v);
    this.deadCount+=5 * noiseV;

    if (this.pos.x > width || this.pos.x < 0) {
      this.pos.x *= -1;
    }

    if (this.pos.y > height || this.pos.y < 0) {
      this.pos.y *= -1;
    }
  }
}

