// language paperscript

var width = view.viewSize.width, height = view.viewSize.height;
// build a regular triangle matrix grid
var grid = [], gridCountV = 40, cellSize = height / gridCountV * 2;
var cellHeight = cellSize / 2 * Math.sqrt(3), gridCountH = Math.floor(width / cellHeight / 2);
for(var row = 0; row <= gridCountV; row++) {
    var offsetX = row % 2 == 0 ? 0 : cellHeight;
    var arr = [];
    for(var col = 0; col < gridCountH; col++) {
        arr.push(new Point(col * cellHeight * 2 + offsetX, row * cellSize / 2));
    }
    arr.reverse();
    grid.push(arr);
}
grid.reverse();

// create boxes
var boxes = [];

var decider = 90;
for(var row = 0, repeat = false; row < grid.length; row += 2) {
    if(row >= grid.length - 2 && !repeat) {
        row = 1;
        decider = 90;
        repeat = true;
    }
    for(var col = 0; col < grid[row].length; col++) {
        // decide whether create a new box
        if(Math.random() * 100 < decider) {
            // create a box
            var center = grid[row][col];
            var top = new Path([center, center + [cellHeight, -cellSize/2], center - [0, cellSize], center + [-cellHeight, -cellSize/2]]);
            top.fillColor = '#F8FCDA';
            top.strokeColor = 'white';
            top.fillColor.saturation = top.fillColor.saturation * Math.min((row + col) / 40, 1);
            var left = new Path([center, center + [0, cellSize], center + [-cellHeight, cellSize/2], center + [-cellHeight, -cellSize/2]]);
            left.fillColor = '#FFAFCC';
            left.strokeColor = 'white';
            left.fillColor.saturation = left.fillColor.saturation * Math.min((row + col) / 40, 1);
            var right = new Path([center, center + [0, cellSize], center + [cellHeight, cellSize/2], center + [cellHeight, -cellSize/2]]);
            right.fillColor = '#A2D2FF';
            right.strokeColor = 'white';
            right.fillColor.saturation = right.fillColor.saturation * Math.min((row + col) / 40, 1);
            var box = new Group({
                children: [top, left, right],
                strokeWidth: 2,
                strokeColor: 'white',
                strokeCap: 'round',
                strokeJoin: 'round',
                position: center,
                blendMode: 'multiply'
            });
            boxes.push(box);
        }
        decider = Math.max(decider - 0.5, 10);
    }
}

