function draw() {
  // rotate(frameCount);
  translate(width / 2, height / 2);
  if (width > height) {
    scale(height / 800);
  }
  else {
    scale(width / 800);
  }
  watercolor();
  flower();
  noLoop();
}

function flower() {
  push();
  let lastX = -999;
  let lastY = 0;
  noFill();
  strokeWeight(2);
  for (let a = 0; a <= 1440; a += 5) {
    let r = a * 0.1 + noise(a) * 100 - 200;
    let x = r * cos(a);
    let y = r * sin(a);
    if (lastX > -999) {
      line(x, y, lastX, lastY);
    }
    lastX = x;
    lastY = y;
  }
  pop();
}

function watercolor() {
  push();
  scale(0.8);
  rotate(random(0, 180));
  for (let i = 0; i <= 10; i++) {
    noStroke();
    fill(map(i, 0, 10, 255, 220), map(i, 0, 10, 140, 20), map(i, 0, 10, 100, 140), 20);
    let xoff = 0;
    let yoff = 0;
    beginShape();
    for (let a = 0; a < 360; a += 0.1) {
      let r = noise(cos(a), sin(a), millis() * 0.05) * 300;
      xoff = r * cos(a);
      yoff = r * sin(a);
      curveVertex(xoff, yoff);
    }
    endShape(CLOSE);
  }
  pop();
}

