function draw() {
  background(p.background);
  noStroke();
  drawFace();
}



function drawEye(faceX, faceY, faceS) {

  let x = map(p.leftEye.x, -1, 1, -faceS, faceS) + faceX
  let y = map(p.leftEye.y, -1, 1, -faceS, faceS)+ faceY
  let a = 15;
  let size = p.leftEyeSize
  for (let i = 0; i < 50; i++) {
    fill(255, (a += 1));
    ellipse(x, y, size *= 0.98);
  }

  x = map(p.rightEye.x, -1, 1, -faceS, faceS) + faceX
  y = map(p.rightEye.y, -1, 1, -faceS, faceS)+ faceY
  a = 15;
  size = p.rightEyeSize
  for (let i = 0; i < 30; i++) {
    fill(255, (a += 1));
    ellipse(x, y, size *= 0.93);
  }
}

function drawFace() {


  let size = p.faceSize*2
  let x = map(p.facePos.x, -1, 1, rangeX.x, rangeX.y)
  let y = map(p.facePos.y, -1, 1, rangeY.x, rangeY.y)
  let a = 15;
  for (let i = 0; i < 50; i++) {
    fill(0, (a += 1));
    ellipse(x, 400, size *= 0.98, size*p.faceShape);
  }
  size = p.faceSize
  for (let i = 0; i < 50; i++) {
    fill(0, (a += 1));
    ellipse(x, y, size *= 0.98, size*p.faceShape);
  }

  drawEye(x, y, size/2 );
  drawMouth(x, y, size/2 );
}

function drawMouth(faceX, faceY, faceS) {
  push();
  let x = map(p.mouthPos.x, -1, 1, -faceS, faceS) + faceX

  let size = p.mouthSize
  let y = map(p.mouthPos.y, -1, 1, -faceS, faceS)+ faceY - size/4
  let a = 0;
  for (let i = 0; i < 10; i++) {
    strokeWeight(30 - 2 * i);
    stroke(255, (a += 5));
    noFill();
    arc(x, y, size, size, 30, 180);
  }

  pop();
}

