var inc = 0.1;
var scl = 2;
var cols, rows;
let colorPalette = ['#000000', '#75201D', '#1D0D53', '#8F5826', '#BE6A3D', '#C74235', '#442B34', '#171717', '#0D0C0C']
let colorVal = 0


function setup() {
  createCanvas(500, 500);
  cols = floor(width / scl);
  rows = floor(height / scl);
  colorMode(HSB);
  noStroke();
  noLoop();
}

function draw() {
  background(0);
  var yoff = 0;
  for (var y = 0; y < rows; y++) {
    var xoff = 0;
    for (var x = 0; x < cols; x ++) {
      var index = (x + y * width) * 4;
      var r = noise(xoff, yoff) * 255;
      const color = getColor();
      xoff += inc;
      fill(color);
      rect(x * scl, y * scl, scl, scl);
    }
  yoff += inc;
  }
}

function getColor() {
  let c = colorPalette[colorVal];
  colorVal++;
  if (colorVal > colorPalette.length) {
    colorPalette = shuffle(colorPalette);
    c = colorPalette[0];
    colorVal = 1;
  }
  return c;
}


