class inkParticle {
  constructor() {
    this.startRadius = 0;
    this.radius = min(random(0, 50), random(0, 50), random(0, 50), random(0, 50), random(0, 50)); //Strong low bias
    this.startX = (random(0, windowWidth) + random(0, windowWidth) + random(0, windowWidth) + random(0, windowWidth)) * 0.25; //Mid Bias
    this.startY = (random(0, windowHeight) + random(0, windowHeight) + random(0, windowHeight) + random(0, windowHeight)) * 0.25; //Mid Bias
    this.rotationDeg = 0;

    this.rVariance = max(random(200, 255), random(200, 255), random(200, 255), random(200, 255));

    this.bVariance = min(random(50, 255), random(50, 255), random(50, 255), random(50, 255));
  }

  drawCircle() {
    let distance = dist(this.startX, this.startY, windowWidth/2, windowHeight/2);

    push();
      fill(this.rVariance, 235, this.bVariance, 255 - floor(distance));
      translate(this.startX, this.startY);
      ellipse(0, 0, this.startRadius, this.startRadius);
    pop();
  }

  animate() {
    this.startRadius = lerp(this.startRadius, this.radius, 0.075);
  }
}

