let ball;
let bounds;
let bounceWalls;
let obstacles;
let scoreBox;
let left_wall;
let top_wall;
let bottom_wall;

let wallColor = "rgb(92,0,171)";
let dangerColor = "rgb(246,92,246)";

// Random path opening
let openingHeight;

let ballSpeed = 0;
let ballPos_y;

let moveSpeed = 3; // how fast player can move up and down
let score = 0;
let alreadyBounced = false; // score +1 only each hit
let start = false; // replayable
let generate = false; // no new layout on first play

function setup() {
  createCanvas(600, 400);

  ball = createSprite(30, height/2, 20, 20);
  ball.setSpeed(ballSpeed, 180);
  ballPos_y = height/2;
  ball.shapeColor = '#003EAC';

  openingHeight = random(height*1.7);

  obstacles = new Group();
  initObstacles();

  bounds = new Group();
  initBounds();

  bounceWalls = new Group();
  initBounceWalls();

}

function draw() {
  background("rgb(229,218,255)");

  ball.bounce(bounceWalls);

  if (keyIsDown(UP_ARROW)){
    ballPos_y -= moveSpeed;
  }
  if (keyIsDown(DOWN_ARROW)){
    ballPos_y += moveSpeed;
  }

  ball.position.y = ballPos_y;

  ball.collide(bounds);

  // Increase speed each time left wall is bounced from
  if (ball.overlap(left_wall)) {
    ballSpeed += 0.5;
    moveSpeed += 0.5;
    ball.setSpeed(ballSpeed, 0);
    // console.log(ballSpeed);
    console.log("faster!");

  }

  if (ball.overlap(bounceWalls) && alreadyBounced === false) {
    openingHeight = random(height*1.7);
    // console.log(openingHeight);
    // console.log("bounce");

    top_wall.height = openingHeight;
    bottom_wall.height = height*1.7 - openingHeight;

    score += 1;
    alreadyBounced = true;
  } else if (ball.position.x >= 40 && ball.position.x <= height - 40) {
    alreadyBounced = false;
  }

  drawSprites();

  // SCORE BOARD
  push();
    fill("rgb(229,218,255)");
    noStroke();
    rectMode(CENTER);
    rect(width - 15, 15, 20, 20);

    fill("#003EAC");
    textAlign(CENTER, CENTER);
    text(`${score}`, width - 15, 16);
  pop();

  // LOSING MESSAGE
  if (ball.overlap(obstacles)) {
    // console.log("You lose!");
    start = false;
    ball.setSpeed(0);
    youLoseMessage();

    if (keyIsDown(UP_ARROW)){
      ballPos_y = height/2;
    }
    if (keyIsDown(DOWN_ARROW)){
      ballPos_y = height/2;
    }

  }

  // INSTRUCTIONS
  push();
    fill("rgb(229,218,255)");
    textAlign(CENTER, CENTER);
    text("Click to start/restart, use the arrows to move up and down", width/2, height - 4);
  pop();
}

function initObstacles() {
  top_wall = createSprite(width/2, 0, 20, openingHeight);
  top_wall.immovable = true;
  top_wall.shapeColor = dangerColor;
  obstacles.add(top_wall);

  bottom_wall = createSprite(width/2, height, 20, height*1.7 - openingHeight);
  bottom_wall.immovable = true;
  bottom_wall.shapeColor = dangerColor;
  obstacles.add(bottom_wall);
}

function initBounceWalls() {
  left_wall = createSprite(5, height * 0.5, 10, height);
  left_wall.immovable = true;
  left_wall.shapeColor = wallColor;
  bounceWalls.add(left_wall);

  const right_wall = createSprite(width - 5, height * 0.5, 10, height);
  right_wall.immovable = true;
  right_wall.shapeColor = wallColor;
  bounceWalls.add(right_wall);

  scoreBox = createSprite(width - 20, 20, 20, 20);
  scoreBox.immovable = true;
  scoreBox.shapeColor = wallColor;
  bounceWalls.add(scoreBox);
}

function initBounds() {
  const top_bound = createSprite(width * 0.5, 6, width, 12);
  top_bound.immovable = true;
  top_bound.shapeColor = wallColor;
  bounds.add(top_bound);

  const bottom_bound = createSprite(width * 0.5, height - 6, width, 12);
  bottom_bound.immovable = true;
  bottom_bound.shapeColor = wallColor;
  bounds.add(bottom_bound);
}

function mouseClicked() {
  if(start === false){
    ball.position.x = 30;
    ball.position.y = height/2;
    ballSpeed = 2;
    moveSpeed = 3
    ball.setSpeed(ballSpeed, 0);

    if (generate){
      openingHeight = random(height*1.7);
    top_wall.height = openingHeight;
    bottom_wall.height = height*1.7 - openingHeight;
    }

    score = 0;
    start = true;
    generate = true;
  }
}

function youLoseMessage() {
  push();
      noStroke();
      fill("rgba(0,0,0,0.69)");
      rect(0, 0, width, height);

      fill("rgba(0,0,0,0.4)");
      rectMode(CENTER);
      rect(width/2, height/2, 300, 100);

      fill(dangerColor);
      textSize(40);
      textAlign(CENTER, CENTER);
      text("YOU LOSE", width/2, height/2 - 10);
      textSize(20);
      text(`Score: ${score}`, width/2, height/2 + 30);
    pop();
}

