function setupSliders(){
  // lineweight
  lw = createSlider(0, 100, 10, 1);
  lw.position(width - 120, 30);
  lw.style('width', '100px');
  // radius
  r = createSlider(0, width / 2, width / 4, 1);
  r.position(width - 120, 70);
  r.style('width', '100px');
  // points
  p = createSlider(0, 100, 3, 1);
  p.position(width - 120, 110);
  p.style('width', '100px');
  // points
  w = createSlider(10, 200, 3, 10);
  w.position(width - 120, 150);
  w.style('width', '100px');
  // radial
  rad = createCheckbox('radial', false);
  rad.position(width - 120, 190);
  rad.changed(radialBox);
  // close shape
  c = createCheckbox('closed', false);
  c.position(width - 120, 230);
  c.changed(closedBox);
