function radialBox(){
  if(this.checked()){
    radial = true;
  } else {
    radial = false;
  }
}

function closedBox(){
  if(this.checked()){
    close = true;
  } else {
    close = false;
  }
}

function constrainBox(){
  if(this.checked()){
    constrain = true;
  } else {
    constrain = false;
  }
}

function getPoints(){
  let x1;
  let y1;
  let x2;
  let y2;
  let xA;
  let yA;
  let xB;
  let yB;

  if(radial){
    // fill up the set with a bunch of points around a circle
    for(let i = 0; i < 2 * PI; i += 2 * PI / points){
      // if(constrain){
        /*
        // organized set points
        let x2 = radius * cos(i);
        let y2 = radius * sin(i);
        let xA = r,
        let yA = random(-height, height);
