        curvePoints.add({x1, y1, x2, y2, xA, yA, xB, yB});
        x1 = x2;
        y1 = y2;
        */
      //} else {
        // randomized set points
        x2 = radius * cos(i);
        y2 = radius * sin(i);
        xA = random(-width / wiggle, width / wiggle);
        yA = random(-height / wiggle, height / wiggle);
        xB = random(-width / wiggle, width / wiggle);
