  }

  let cX = width/2;
  let cY = height/4-20;

  let w = 20;         // 1/2 Line width

  let sx = 10;       // Shift amount of top x and bottom x



  let XpWarp = 1.1; // Perspective warp Y



  speed += acc;





  let rx = r * cos(ang) + off;
  let ry = r * sin(ang);

  let dw = d * cos(ang) + off;
  let dh = d * sin(ang);

    //CENTER
    fill(214, 193, 54, alpha);


  quad(
    cX - w/2 + off , cY + ry,
    cX + w/2 + off , cY + ry,
    cX + w/2 + off , cY + h + ry + dh*0.7,
    cX - w/2 + off , cY + h + ry + dh*0.7
  );

  //SIDES 
  fill(255, alpha);

  quad(
    cX -dx + w - rx + sx,        cY + ry,  
    cX -dx - rx + sx,            cY + ry ,
    cX -dx - rx - dw,            cY + h + ry + dh,
    cX -dx - rx + w - dw,        cY + h + ry + dh
  );

    quad(
    cX +dx - w + rx - sx,       cY + ry,  
    cX +dx + rx - sx,           cY + ry,
    cX +dx + rx + dw,           cY + h + ry + dh,
    cX +dx + rx - w + dw,       cY + h + ry + dh
  );


  r+= speed;
  h *= YpWarp;
  d += speed * fov_stretch;



}




