}

function draw() {
  background(50, 50, 75);

  wave1 = sin(frameCount*0.02)*90;
  wave2 = cos(frameCount*0.1)*10;
  const treeCount = 5;
  for(let copy = 0; copy < treeCount; copy++){
    drawTree(width/2, height/2, (360/treeCount)*copy);
  }
}

function drawTree(x, y, angle){
  myTurtle.penUp();
  myTurtle.moveTo(x, y);
  myTurtle.turnTo(angle + wave1);
  myTurtle.penDown();
  // length of first branch (og 28)
  const branchLength = 30;
  drawBranch(branchLength);
}

function drawBranch(length) {
  // this return function determines level of detail
  // higher number = less detail
  if (length < 15) {
    return;
  }

  // draw first branch
  myTurtle.moveForward(length);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(30 + wave2 + wave1);
  // good range * 0.75 to 0.85
  drawBranch(length * 0.85);
  myTurtle.popState();

  // right child
  myTurtle.pushState();
  myTurtle.turnRight(30 + wave2 + wave1);
  // good range * 0.75 to 0.85
  drawBranch(length * 0.85);
  myTurtle.popState();
}

