function pickTerrain(n){
  //let h = noise((x)*noiseScale, (z)*noiseScale);
  if (n < 0.2){  c = ocean; } 
  else if (n < 0.3) { c = shore; } 
  else if (n < 0.4) { c = sand; }
  else if (n < 0.5) { c = grass; }
  else if (n < 0.6) { c = swamp; }
  else if (n < 0.63) { c = stone; }
  else if (n < 0.7) { c = mountain; } 
  else { c = snow; }

  return color(c);

}

