// draw the map
function drawMap() { // return earth points
  let px_num = 0;
  for(let row = 0; row < _row; row++) {
    for(let col = 0; col < _col; col++) {
      let param = noise(_startX + _step * col, _startY + _step * row);
      if(param > _thre) { // boundary of the ocean and the land
        if(param > _hier[3]) {
          fill('#FFFFFF');
          stroke('#FFFFFF');
          _terrData.mountain.push(px_num);
        } else if(param > _hier[2]) {
          fill('#9AB87A');
          stroke('#9AB87A');
          _terrData.plain.push(px_num);
        } else {
          fill('#e5d7bd');
          stroke('#e5d7bd');
          _terrData.offshore.push(px_num);
        }
      } else {
        if(param < _hier[0]) {
          fill('#006080');
          stroke('#006080');
          _terrData.deep_sea.push(px_num);
        } else if(param < _hier[1]) {
          fill('#0096c7');
          stroke('#0096c7');
          _terrData.normal_sea.push(px_num);
        } else {
          fill('#90e0ef');
          stroke('#90e0ef');
          _terrData.shallow_sea.push(px_num);
        }
      }
      point(col * _pxSize, row * _pxSize);

      px_num ++;
    }
  }
  return _terrData.plain.concat(_terrData.mountain, _terrData.offshore);
}
function drawMap_2() {
  let px_num = 0;

  // load every pixel and assign values
  for(let row = 0; row < _row; row++) {
    for(let col = 0; col < _col; col++) {
      let param = noise(_startX + _step * col, _startY + _step * row);
      if(param > _thre) { // boundary of the ocean and the land
        // mountain
        if(param > _hier[3]) _terrData.mountain.push(px_num);
        // plain
        else if(param > _hier[2]) _terrData.plain.push(px_num);
        // offshore
        else _terrData.offshore.push(px_num);
      } else {
        // deep sea
        if(param < _hier[0]) _terrData.deep_sea.push(px_num);
        // normal sea
        else if(param < _hier[1]) _terrData.normal_sea.push(px_num);
        // shallow sea
        else _terrData.shallow_sea.push(px_num);
      }

      px_num ++;
    }
  }
}

// draw towns (return cities)
function drawTowns() {
  let loc = shuffle(_terrData.plain);
  let count = 0, cities = [], city_num = Object.keys(_cityNames).length;
  while(cities.length < city_num) {    
    let x = loc[count] % _col, y = floor(loc[count] / _col);
    // compare the distance between new city and previous cities
    if(cities.length > 0) {
      let adopt = true;
      for(let n in cities) {
        if(dist(x, y, cities[n][0], cities[n][1]) < 100) {
          adopt = false;
          break;
        }
      }
      if(adopt) cities.push([x, y]);
    } else cities.push([x, y]);
    count ++;
  }

  // draw the cities
  push();
  fill(_cityColor);
  stroke('black');
  cities.forEach(pos => circle(pos[0], pos[1], 10));

  fill('black');
  noStroke();
  for(let c in cities) {
    text(_cityNames[c], cities[c][0], cities[c][1] - 12);
  }
  pop();

  return cities;
}
// draw the trove (return trove)
function drawTrove(earth) {
  let trs = earth[round(random(earth.length))];
  let x = trs % _col, y = floor(trs / _col);
  image(_Xfind, x, y, 60, 60);
  return [x, y];
}
// draw the path
function drawPath(trove, cities) {
  // generate 3 way points
  let wayPts = [];
  let locations = shuffle(cities);

  for(let p = 0; p < 3; p++) {
    let loc_city = cities[round(random(cities.length))];
    wayPts.push([loc_city[0], loc_city[1]]);
  }
  wayPts.push(trove); // prepare the locations
  console.log(wayPts);
  push();
  stroke(_pathColor);
  strokeWeight(7);
  for(let j = 0; j < 3; j++) {
    let last = max(j - 1, 0), next = min(j + 2, 3);
    let steps = round(dist(wayPts[j][0], wayPts[j][1], wayPts[j + 1][0], wayPts[j + 1][1]) / _pathInterval);
    // draw the path
    for(let i = 0; i < steps; i++) {
      let t = i / steps;
      let x = curvePoint(wayPts[last][0], wayPts[j][0], wayPts[j + 1][0], wayPts[next][0], t);
      let y = curvePoint(wayPts[last][1], wayPts[j][1], wayPts[j + 1][1], wayPts[next][1], t);
      point(x, y);
    }
  }
  pop();
}

// draw the grid
function drawGrid(size, color) {
  let row = round(height / size), col = round(width / size);
  push();
  stroke(color);
  // draw the warp
  for(let c = 0; c < col; c++) {
    line(c * size, 0, c * size, height);
  }
  // draw the weft
  for(let r = 0; r < col; r++) {
    line(0, r * size, width, r * size);
  }
  pop();
}

