function draw() {
  background(0);

  push();
  translate(width/2, height/2);

  let xOff = 0;

  beginShape();
  for (let a = 0; a < TWO_PI; a+=0.01){
    let offset = (map(noise(xOff), 0, 1, -25, 25))*frameCount;
    let r = 150+ offset;
    let x = r * cos(a);
    let y = r * sin(a);
    vertex(x, y);
    xOff += 0.1;
  }
  endShape();
  pop();
}

