function drawFace(eyeW, eyeH, lipSize) {
  push();
  translate(-120, -100);
  fill(0);
  ellipse(
    noise(frameCount / 80) * 20,
    noise(frameCount / 50 + 10) * 20,
    eyeW,
    eyeH
  );
  ellipse(
    noise(frameCount / 80) * 20 + 70,
    noise(frameCount / 50 + 10) * 20,
    eyeW,
    eyeH
  );
  noFill();
  stroke(0);
  strokeWeight(lipSize);
  arc(
    noise(frameCount / 80) * 20 + 40,
    noise(frameCount / 80) * 20 + 45,
    50,
    50,
    0,
    HALF_PI + QUARTER_PI
  );
  pop();
}

// fcm = circular pace

function drawBody(vNnum, nm, sm, fcm) {
  push();
  rotate(frameCount / fcm);
  let dr = TWO_PI / vNnum;
  beginShape();
  for (let i = 0; i < vNnum + 3; i++) {
    let ind = i % vNnum;
    let rad = dr * ind;
    let r =
      height * 0.16 +
      noise(frameCount / nm + ind) * height * 0.1 +
      sin(frameCount / sm + ind) * height * 0.05;
    curveVertex(cos(rad) * r, sin(rad) * r);
  }
  endShape();
  pop();
}

