// require /turtles/turtle/turtle.js

var turtle; // turtle pen

// draw branches
function drawSpiralBranch(initAngle, dir, length, Hue, pos = null) {

  // reset the position of turtle
  if(pos) {
    turtle.moveTo(pos.x, pos.y);
    turtle.penDown();
  }
  turtle.turnTo(initAngle);

  let curAngle = initAngle; // record the current angle
  let maxWeight = max(length / 100, 1), targtHue = floor(random(360));
  let angleOffset = random(0.8, 1.2);

  for(let m = 0; m < length; m++) {
    let angleChange = map(sq(map(m, 0, length, 0, 1)), 0, 1, 0.5, 3) * (600 / length) * angleOffset;
    if(dir) {
      turtle.turnLeft(angleChange);
      curAngle -= angleChange;
    } else {
      turtle.turnRight(angleChange);
      curAngle += angleChange;
    }

    let newHue = round(lerp(Hue, targtHue, m / length));
    stroke('hsl(' + newHue + ', 60%, ' + round(random(40, 80)) + '%)');
    strokeWeight(map(m, 0, length, maxWeight, 0.2));
    turtle.moveForward(map(m, 0, length, length / 300, length / 500));

    // randomly draw next branch
    let lvl = dist(turtle.x, turtle.y, width / 2, height / 2);
    let stopPt = round(random(50, 100));
    if(m % stopPt === 0 && random() < map(length, 400, 100, 0.3, 0.05) && length > 100 && lvl + length > 400) {
      turtle.pushState();
      drawSpiralBranch(curAngle, !dir, length - 20, newHue);
      turtle.popState();
    }
  }
}

function setup() {
  createCanvas(800, 600);

  turtle = new Turtle(); // create the turtle object
  turtle.penUp();
}

function draw() {
  background(0);

  for(let j = 0; j < 4; j++) {
    for(let i = 0; i < 4; i++) {
      drawSpiralBranch(i * 90, true, 400, 180, {x: random(width), y: random(height)});
      turtle.penUp(); 
    }
  }

  noLoop();
}

