class Text {
  constructor(content, x, y, init_rotate, init_size, bias, move_speed_x, move_speed_y) {
    this.content = content; // text string
    this.x = x; // initial x position
    this.y = y; // initial y position
    this.init_rotate = init_rotate; // initial rotate
    this.init_size = init_size; // initial text size
    this.bias = bias; // newer ones smaller than older ones
    this.move_speed_x = move_speed_x; // move speed & direction
    this.move_speed_y = move_speed_y;
  }

  write() {
    push();
    // text position
    translate(this.x - this.move_speed_x * this.bias, this.y - this.move_speed_y * this.bias);
    rotate(this.init_rotate - rotate_speed * this.bias);
    // text style
    fill(random(colors));
    stroke(0);
    strokeWeight(4);
    textSize(this.init_size - grow_speed * this.bias);
    textAlign(CENTER);
    textFont("Impact");
    // text content
    if ((this.init_size - grow_speed * this.bias) > standard_size) { // show only when larger than standard_size
      text(this.content, 0, 0);
    }
    pop();
  }

  move() {  // move & rotate & grow
    this.x += this.move_speed_x;
    this.y += this.move_speed_y;
    this.init_rotate += rotate_speed;
    this.init_size += grow_speed;
  }

}

