function RenderLines(type, r,g,b) {
  let loopValue = type === 'width' ? width : height;
  for (let j = 0; j < loopValue; j+=stepSize) {
    let opacity = map(j, 0, type === 'width' ? width : height, 0, 255);
    push();
    stroke(r,g,b, opacity);
    type === 'width' ? translate(j, 0) : translate(0, j);
      beginShape();
        noFill();
        for (let i = 0; i < width; i++) {
          let trigVal = type === 'width' ? sin(t)*0.35 : cos(t)*0.4;
          let n = noise(t) + trigVal;
          type === 'width' ? vertex(n*amplitude, i) : vertex(i, n*amplitude);
          t+=.1;
        }
      endShape();
    pop();
  }
}

function makeCircle(numSides, radius) {
  const points = [];
  const radiansPerStep = (Math.PI * 2) / numSides;
  for (let theta = 0; theta < Math.PI * 2; theta += radiansPerStep) {
    const x = 0.5 + radius * Math.cos(theta);
    const y = 0.5 + radius * Math.sin(theta);

    points.push([x, y]);
  }
  return points;
}

