function setup() {
  createCanvas(windowWidth, windowHeight);
  frameRate(10);
}


function draw() {
  background(255);
  textSize(params.textsize);
  funcArray[params.shape](params.word1, params.word2);
}


function wigglyCircle(w1, w2){
  push();
  translate(width / 2, height / 2);

  let width1 = textWidth(w1);
  let width2 = textWidth(w2);

  let radius = params.radius * width2;

  for(let k = -height / 2; k < height / 2; k += params.textsize){
    let s = "";
    let increment = width1;
    let wiggle = sin(frameCount + k) * params.wiggle;
    for(let i = -width / 2; i < width / 2; i += increment){
      if(dist(i, k, 0, 0) < radius){
        s += w2;
        increment = width2;
      }else{
        s += w1;
        increment = width1;
      }
    }
    text(s, -width / 2 + wiggle, k);
  }

  pop();
}

function wigglyTriangles(w1, w2){
  push();
  translate(width / 2, height / 2);

  let width1 = textWidth(w1);
  let width2 = textWidth(w2);

  for(let k = -height / 2; k < height / 2; k += params.textsize){
    let offset = abs(k / params.textsize * width2);
    let wiggle = sin(frameCount + offset) * params.wiggle;
    let s = "";

    for(let i = 0; i < width / 2 - offset / 2; i += width1){
      s += w1;
    }
    for(let i = width / 2 - offset / 2; i < width / 2 + offset / 2; i += width2){
      s += w2;
    }
    for(let i = width / 2 + offset / 2; i < width; i += width1){
      s += w1;
    }
    text(s, -width / 2 + wiggle, k);
  }

  pop();
}

function wigglyStripes(w1, w2){
  let s = createString(w1, w2, floor(params.length1), floor(params.length2));
  let w = textWidth(s);
  for(let i = 0; i < height; i += params.textsize){
    let wiggle = sin(frameCount + i / params.textsize) * params.wiggle;
    for(let k = 0; k < width; k += w){
      text(s, k + wiggle, i);
    }
  }
}

function createString(w1, w2, l1, l2){
  let s = "";
  for(let i = 0; i < l1; i++){
    s = s + w1;
  }
  for(let i = 0; i < l2; i++){
    s = s + w2;
  }
  for(let i = 0; i < l1; i++){
    s = s + w1;
  }

  return s;
}

