function draw() {

  let spectrum = fft.analyze(); 
  //noStroke();
   background(0);
    translate(width/2.6, height/5);

  for(let j=0; j< 5; j++){

    translate(width/2.4, height/2.4);
    colorMode(HSB);
    rotate(145);
    for (let i = 0; i < spectrum.length; i++){
    let angle = map(i, 0, spectrum.length, 0, 720); 
