window.onload = function () {
    // Get a reference to the canvas object
    const canvas = document.getElementById("myCanvas");
    // Create an empty project and a view for the canvas:
    paper.setup(canvas);

    const center = new paper.Point(100, 50);
    drawPow(125, 225);
    drawPow(125, -55);
    drawCos();

    function drawPow (startX, endX) {
      let myPaths = [];
      for(let i = 0; i < 18; i += 0.5){
        myPaths[i] = new paper.Path();
        myPaths[i].strokeColor = "blue";

        myPaths[i].moveTo(center.add([endX + i*5, Math.pow(i, 1.9)]));
        myPaths[i].lineTo(center.add([startX, 200]));
      }
    }
    function drawCos () {
      let myPaths = [];
      for(let i = 0; i < 50; i += 0.5){
        myPaths[i] = new paper.Path();
        myPaths[i].strokeColor = "red";

        myPaths[i].moveTo(center.add([i*10 - 100, 300 + Math.cos(i)*7]));
        myPaths[i].lineTo(center.add([150, 425]));
      }
    }


    // Draw the view now:
    paper.view.draw();
  };

