class customShape {
  constructor(x,y,w) {
    this.x = x;
    this.y = y;
    this.w = w;
    this.randomRFactor = random(['standard', 'invert']);
    this.trianglePosition = random([1,2,3,4]);
  }

  draw() {
    push();
      blendMode(MULTIPLY);
      fill('#4A148C');
      translate(this.x + gridFactor/2, this.y);
      let rotateFactor;
      if (this.randomRFactor === 'standard') {
        rotateFactor = map(sin(frameCount * 1.2), -1, 1, -90, 90);
      }
      else {
        rotateFactor = map(cos(frameCount * 1.2), -1, 1, -90, 90);
      }
      rotate(rotateFactor);
      noStroke();
      rect(0, 0, this.w, this.w, 7);
      push();
        blendMode(LIGHTEST);
        if (this.trianglePosition === 1) {
          fill('#FFEB3B');
          triangle(0, 0, this.w/2, this.w/2, this.w/2, 0);
        }
        else if (this.trianglePosition === 2) {
          fill('#E91E63');
          rotate(90)
          triangle(0, 0, this.w/2, this.w/2, this.w/2, 0);
        }
        else if (this.trianglePosition === 3) {
          fill('#E91E63');
          rotate(180)
          triangle(0, 0, this.w/2, this.w/2, this.w/2, 0);
        }
        else {
          fill('#FFEB3B');
          rotate(-90)
          triangle(0, 0, this.w/2, this.w/2, this.w/2, 0); 
        }
      pop();
    pop();
  }
}

