function checkCubesDrawn(){
  let count = 0;
  for(let i = 0; i < cubes.length; i++){
    if(cubes[i].drawn == true){
      count++;
    }
  }
  if(count > cubes.length - 50){
    for(let i = 0; i < cubes.length; i++){
      if(cubes[i].drawn != true){
        cubes.drawn = true;
      }
    }
    return true;
  }
}

function checkCubesDone(){
  let count = 0;
  for(let i = 0; i < cubes.length; i++){
    if(cubes[i].done == true){
      count++;
    }
  }
  return(count === cubes.length);
}

function checkCubesGrown(){
  let count = 0;
  for(let i = 0; i < cubes.length; i++){
    if(cubes[i].grown == true){
      count++;
    }
  }
  return(count === cubes.length);
}


function placeCubes(){
  push();
  translate(-width / 2 + PIXELSIZE, -height / 2 - PIXELSIZE, 100);
  for(let i = 0; i < cubes.length; i++){
    if(random() < 0.05 && cubes[i].drawn == false){
      cubes[i].drawn = true;
    }
    cubes[i].drawFirst();
  }
  pop();
}

function spinnyText(){
  push();
  translate(-width / 2 + PIXELSIZE, -height / 2 - PIXELSIZE, 100);
  for(let i = 0; i < cubes.length; i++){
    cubes[i].drawn = true;
    cubes[i].draw();
  }
  pop();
}

function Cube(x, y, size, color, invert) {
  this.x = x;
  this.y = y;
  this.xIndex = 0;
  this.yIndex = 0;
  this.xOffset = 0;
  this.yOffset = 0;
  this.size = size;
  this.color = color;
  this.invert = invert;
  this.rInc = random([0.0015625, 0.003125, 0.00625]);
  this.rotationMax = random([1, 2, 3]) * 2 * PI + PI;
  this.drawn = false;
  this.done = false;
  this.grown = false;
  this.rotations = 0;
  this.angleA = 0;
  this.angleB = 0;
}

Cube.prototype.reset = function(){
  this.rotations = 0;
  this.angle = 0;
  this.done = false;
}

Cube.prototype.drawFirst = function(){
  if(this.drawn){
    push();
    translate(this.x, this.y);
    this.angleA += this.rInc * SPINSPEED;
    rotateY(this.angleA);
    noStroke();
    stroke('black');
    fill(this.color);
    box(this.size, this.size, this.size);
    pop(); 
  }
}

Cube.prototype.draw = function(){
  if(this.drawn){
    push();
    translate(this.x, this.y);
    if(!this.done){
      this.angleB += this.rInc * SPINSPEED;
      if(this.angleB >= this.rotationMax){
        this.done = true;
      }
    }
    if(this.size < PIXELSIZE){
      this.size += 0.06;
    }
    rotateY(this.angleB);
      // noStroke();
    stroke('black');
    fill(this.color);
    box(this.size, this.size, this.size);
    if(this.invert){
      fill('red');
      noStroke();
      // stroke('black');
      translate(0, 0, -this.size / 2 - 1);
      plane(this.size, this.size);
    }
    pop(); 
  }
}

Cube.prototype.grow = function(){
  if(this.size < PIXELSIZE){
    this.size += 0.1;
  }
  if(this.drawn){
    push();
    translate(this.x, this.y);
    // stroke('black');
    noStroke();
    fill(this.color);
    box(this.size, this.size, this.size);
    if(this.invert){
      fill('red');
      noStroke();
      // stroke('black');
      translate(0, 0, this.size / 2 + 1);
      plane(this.size, this.size);
    }
    pop(); 
  }
}

// saveFrame - a utility function to save the current frame out with a nicely formatted name
// format: name_####.extension
// name: prefix for file name
// frameNumber: number for the frame, will be zero padded
// extension: jpg or png, controls file name and image format
// maxFrame: checked against frameNumber, frames beyond maxFrame are not saved
function saveFrame(name, frameNumber, extension, maxFrame) {
  // don't save frames once we reach the max
  if (maxFrame && frameNumber > maxFrame) {
    return;
  }

  if (!extension) {
    extension = "png";
  }
  // remove the decimal part (just in case)
  frameNumber = floor(frameNumber);
  // zero-pad the number (e.g. 13 -> 0013);
  var paddedNumber = ("0000" + frameNumber).substr(-4, 4);

  save(name + "_" + paddedNumber + "." + extension);
}

// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js
// require /turtles/turtle/turtle.js

const circuits = [];
var turtle;

class circuit {
  constructor(x, y, dir, steps, sWidth, sColor) {
    this.ox = x;
    this.oy = y;
    this.x = x;
    this.y = y;
    this.dir = dir;
    this.weight = sWidth;
    this.color = sColor;
    this.buildCircuit.call(turtle, this, steps, sWidth, sColor);
  }

  // build a circuit
  buildCircuit(thread, steps, sWidth, sColor) {
    strokeWeight(sWidth);
    stroke(sColor);
    this.moveTo(thread.x, thread.y);
    this.turnTo(thread.dir);
    this.penDown();
    for(let step = 0; step < steps; step++) {   
      // decide how long to move
      let distance = random(40, 160);

      switch(thread.dir / 90 % 4) {
        case 0: // right
          thread.x += distance;
          break;
        case 1: // down
          thread.y += distance;
          break;
        case 2: // left
          thread.x -= distance;
          break;
        case 3: // up
          thread.y -= distance;
          break;
      }

      if(thread.x < 0 || thread.x > width || thread.y < 0 || thread.y > height) {
        // draw terminal
        turtle.pushState();
        drawTerminal(turtle, thread.dir, thread.weight);
        turtle.popState();
        break;
      } else {
        // decide if to turn
        if(step > 0) {
          let turnDir = random(3);
          if(turnDir < 1) {
            this.turnRight(90);
            thread.dir += 90;
          } else if(turnDir < 2) {
            this.turnLeft(90);
            thread.dir -= 90;
          }
        }
        this.moveForward(distance);
      }
    }
    this.penUp();
  }
}

// draw circle
function drawTerminal(turtle, dir, weight) {
  stroke(round(random(120, 200)));
  strokeWeight(max(weight - 1, 3));
  turtle.turnRight(90);
  for(let i = 0; i < 120; i++) {
    turtle.turnLeft(15 - weight);
    turtle.moveForward(1);
  }
}

function setup() {
  createCanvas(800, 600);
  rectMode(CENTER);

  turtle = new Turtle(); // create the turtle object
  turtle.penUp();
}

function draw() {
  background(0);

  // create circuits
  for(let i = 0; i < 25; i++) {
    let x = width/2, y = height/2, dir = 0;
    let randomColor = color('hsl(' + round(random(360)) + ', 50%, 60%)');
    // decide the start position
    switch(floor(random(4))) {
      case 0: // left
        x = 0;
        y = random(height);
        dir = 0;
        break;
      case 1: // top
        x = random(height);
        y = 0;
        dir = 90;
        break;
      case 2: // right
        x = width;
        y = random(height);
        dir = 180;
        break;
      case 3: // bottom
        x = random(width);
        y = height;
        dir = 270;
        break;
    }
    let steps = round(random(15, 25));
    circuits.push(new circuit(x, y, dir, 200, round(random(2, 10)), randomColor));
  }

  noLoop();
}

