}

function draw() {
  for (var i = 0; i < 3; i++) {
    for (var j = 0; j < 3; j++) {
      t.penUp();
      t.moveTo(400 * i, 130 + 400 * j);
      t.turnTo(-70);
      t.penDown();
      drawBranch(100);
    }
  }
  for (var i = 0; i < 3; i++) {
    for (var j = 0; j < 3; j++) {
      drawPetal(200 + 400 * i, 250 + 400 * j);
    }
  }
  // Tong's addition

  for (var i = 0; i < 2; i++) {
    for (var j = 0; j < 3; j++) {
      turtle.penUp();

      drawSpiralBranch(270, random() < 0.5 ? true : false, 300, 255, {
        x: 200 + i * 400,
        y: 100 + j * 400,
      });
    }
  }
  for (var i = 0; i < 3; i++) {
    for (var j = 0; j < 2; j++) {
      turtle.penUp();

      drawSpiralBranch(270, random() < 0.5 ? true : false, 300, 255, {
        x: i * 400,
        y: 300 + j * 400,
      });
    }
  }
}

function drawBranch(length) {
  if (length < 20) {
    return;
  }
  strokeWeight(length / 30);

  t.moveForward(length * 0.25);
  t.turnLeft(10);
  t.moveForward(length * 0.25);
  t.turnRight(10);

  t.pushState();
  t.turnLeft(random(20, 40));
  drawBranch(length * 0.6);
  t.popState();

  t.pushState();
  t.turnRight(random(10, 30));
  drawBranch(length * 0.6);
  if (length < 30) {
    for (var i = 0; i < 18; i++) {
      t.moveForward(1);
      t.turnRight(6);
    }
    t.turnRight(80);
    for (var i = 0; i < 18; i++) {
      t.moveForward(1);
      t.turnRight(6);
    }
  }
  t.popState();
}

function drawPetal(startX, startY) {
  strokeWeight(1);
  f.penUp();
  f.moveTo(startX, startY);
  f.penDown();
  for (var j = 0; j < 10; j++) {
    for (var i = 0; i < 24; i++) {
      f.moveForward(random(2, 5));
      f.turnRight(5);
    }
    for (var i = 0; i < 36; i++) {
      f.turnRight(5);
      f.moveForward(random(1, 3));
    }
  }
}

function drawSpiralBranch(initAngle, dir, length, i = 255, pos = null) {
  push();
  // reset the position of turtle
  if (pos) {
    turtle.moveTo(pos.x, pos.y);
    turtle.penDown();
  }
  turtle.turnTo(initAngle);

  // stroke(255, i);
  strokeWeight(max(length / 200, 1));

  let curAngle = initAngle; // record the current angle
  let angleOffset = random(0.8, 1.2);

  for (let m = 0; m < length; m++) {
    let angleChange =
      map(sq(map(m, 0, length, 0, 1)), 0, 1, 0.5, 3) *
      (600 / length) *
      angleOffset;
    if (dir) {
      turtle.turnLeft(angleChange);
      curAngle -= angleChange;
    } else {
      turtle.turnRight(angleChange);
      curAngle += angleChange;
    }
    turtle.moveForward(map(m, 0, length, length / 300, length / 500));

    // randomly draw next branch
    if (m < length * 0.3 && m % 20 === 0 && random() < 0.3 && length > 50) {
      turtle.pushState();
      drawSpiralBranch(curAngle, !dir, length - 50, max(120, i - 30));
      turtle.popState();
    }
  }
  pop();
}

