    overImg.set(n *100, n * offset, c);
    offset++;
  }

  img.updatePixels();
  overImg.updatePixels();

  noSmooth();
  image(img, 0, 0, width, height);
  image(overImg, 0, 0, width, height);
  noLoop();
}

