  h = w + random([-5, 0, 5]);
  d = random([10, 12, 14, 16, 18, 20]);

  for (let j = 0; j < height + h; j += h / 4) {
    push();
    if (((j / h) * 4) % 2 == 0) {
      translate(0, j);
      iHorizontal();
    } else {
      translate(w / 2, j);
      iHorizontal();
    }
    pop();
  }
}

// a horizontal line of circles
function iHorizontal() {
  for (let i = 0; i < width + w; i += w) {
    cCircle(i + random(6), 0 + random(6), w, h, d);
  }
}

// concentricCircle
function cCircle(a, b, w, h, d) {
  // a&b for translate, w&h for ellipse, d for circle distance
  push();
  translate(a, b);
  //color = ["#08ff08", "#fe1493", "#7BFAFD"];
  color = [params.color1, params.color2];
  color1 = random(color);
  color2 = random(color);
  for (let i = 0; (d * i < w) & (d * 1 < h); i++) {
    if (i % 2 == 0) {
      fill(color1);
      ellipse(0, 0, w - d * i, h - d * i);
    } else {
      fill(color2);
      ellipse(0, 0, w - d * i, h - d * i);
    }
  }
  pop();
}

