        beginShape();
        for (let a = 0; a < TWO_PI; a += radians(1)) {
            let r = 5
            let x1 = 2 * cos(a) * (sin(a) + 1) - 20
            let y1 = 1 * sin(a) * r
            //diamond 2
            vertex(x1, y1)

        }
        endShape(CLOSE);
        pop();
    }
}

