// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js

var _row = 24, _col = 24;
var _size = 0;

var _patternCount = 0, _patterns = [1, 2, 3, 4, 5, 6];
var _paletteCount = 0;
var _paletteBottom = ['#cb997e', '#ddbea9', '#ffe8d6', '#b7b7a4', '#a5a58d', '#6b705c'];
var _paletteUp = ['#edede9', '#d6ccc2', '#f5ebe0', '#e3d5ca', '#d5bdaf', '#ededed'];

function setup() {
  createCanvas(640, 640);

  _size = width / _row;
  _col = ceil(height / _size);

  _patterns = shuffle(_patterns);
  _paletteBottom = shuffle(_paletteBottom);
  _paletteUp = shuffle(_paletteUp);

  noLoop();
}

function draw() {
  background(255);

  // draw patterns
  stroke(220);
  for(let row = 0; row < _row; row++) {
    for(let col = 0; col < _col; col++) {
      fill(_paletteBottom[_paletteCount]);
      rect(col * _size, row * _size, _size);
      drawPattern(col * _size, row * _size, _size, _paletteUp[_paletteCount], _patterns[_patternCount]);
      // check the pattern counter
      if(_patternCount >= 5) {
        _patternCount = 0;
        _patterns = shuffle(_patterns);
      } else _patternCount ++;

      // check the palette counter
      if(_paletteCount >= _paletteBottom.length - 1) {
        _paletteCount = 0;
        _paletteBottom = shuffle(_paletteBottom);
        _paletteUp = shuffle(_paletteUp);
      } else _paletteCount ++;
    }
  }
}

function drawPatterns() {

}

function drawPattern(x, y, s, color, style) {
  push();
  noStroke();
  translate(x + s/2, y + s/2);
  fill(color);
  switch(style) {
    case 1: // normal circle
      ellipse(0, 0, s * 0.7);
      break;
    case 2: // verticle quad
      quad(0, s * -0.4, s * 0.25, 0, 0, s * 0.4, s * -0.25, 0);
      break;
    case 3: // horizontal quad
      quad(0, s * -0.25, s * 0.4, 0, 0, s * 0.25, s * -0.4, 0);
      break;
    case 4: // triangle up
      triangle(0, s * -0.35, s * 0.3, s * 0.3, s * -0.3, s * 0.3);
      break;
    case 5: // triangle side
      triangle(s * 0.35, 0, s * -0.3, s * -0.3, s * -0.3, s * 0.3);
      break;
    case 6: // ring
      noFill();
      stroke(color);
      strokeWeight(3);
      ellipse(0, 0, s * 0.6);
      break;
    default:
      // console.log("Fail to generate.");
  }
  pop();
}

