var animateColor = new Color("#f94144");
function creatMoss(basePoint, reverse1, reverse2) {
  for (var i = 0; i < 5; i++) {
    var point1 = new Point(0, 100) + basePoint;
    var size1 = new Size(40, 40);
    var path1 = new Path.Ellipse(point1, size1);
    var point2 = new Point(30, 100) + basePoint;
    var size2 = new Size(40, 40);
    var path2 = new Path.Ellipse(point2, size2);
    var comboPathLeft = path1.intersect(path2);
    var comboPathRight = path1.intersect(path2);
    comboPathLeft.rotate(-45);
    comboPathRight.rotate(45);
    comboPathRight.translate(20, 0);
    comboPathLeft.onFrame = function (event) {
      if (event.count % 60 == 1) {
        animateColor.hue++;
        this.set({
          fillColor: animateColor,
        });
      }
    };
    comboPathRight.fillColor = new Color("#f94144");
    comboPathLeft.translate(0, i * 15);
    comboPathRight.translate(0, i * 15);
  }
  var path = new Path();
  path.strokeColor = "#f94144";
  path.add(new Point(45, 45) + basePoint);
  path.add(new Point(45, 190) + basePoint);
  path.strokeWidth = 2;

  var from = new Point(45, 45) + basePoint;
  var through = new Point(55 - reverse1, 30) + basePoint;
  var to = new Point(70 - reverse2, 45) + basePoint;
  var path = new Path.Arc(from, through, to);
  path.strokeColor = "#f94144";
  path.strokeWidth = 2;

  var point = new Point(60 - reverse2, 45) + basePoint;
  var size = new Size(20, 20);
  var path = new Path.Ellipse(point, size);
  // path.fillColor = new Color("#f94144");
  path.onFrame = function (event) {
    if (event.count % 60 == 1) {
      animateColor.hue++;
      this.set({
        fillColor: animateColor,
      });
    }
  };
}

